################# Combien de handles contiennent un numero #####################################

def yanumero(word)
	ya=0
	for i in 0..9
		my_match = /#{i.to_s}/.match(word)
		if(my_match!=nil)
			ya=ya+1
		end
	end
	if(ya> 0)
		return true 
	else
		return false
	end
end

nbgens=0
for journaliste in handles
	test=yanumero(journaliste)
	if test
		nbgens=nbgens+1
	end
end

puts("Dans la liste, il y a #{nbgens} personnes qui ont mis un numero dans leur adresse.")

################## Combien de Aude dans la liste ####################################
def yaAude(word)
	word=word.downcase
	my_match = /aude/.match(word)
	if(my_match==nil)
		return false 
	else
		return true
	end
end

nbAude=0
for journaliste in handles
	test=yaAude(journaliste)
	if test
		nbAude=nbAude+1
	end
end

puts("Il y en a #{nbAude} qui s'appellent certainement Aude.")

