puts("Il y en a #{nbMaj} qui contiennent une majuscule.")


############# Combien de _ en tout ##################################
def count_underscore(word)
	i=0
	for k in 1..word.size()
		if word[k-1]=="_"
			i = i+1
		end
	end
	return(i)
end


nbunderscore=0
for journaliste in handles
	jojo = count_underscore(journaliste)
	nbunderscore = nbunderscore+jojo
end
puts("En tout il y a #{nbunderscore} underscore. ")

